window.Store={init(){document.addEventListener("alpine:init",()=>{Alpine.store("global",{user:null,theme:"light",params:{},setUser(e){this.user=e},setTheme(e){this.theme=e}})})},get:e=>Alpine.store("global")[e],set(e,t){Alpine.store("global")[e]=t},subscribe(e,t){document.addEventListener("alpine:initialized",()=>{Alpine.effect(()=>{const i=Alpine.store("global")[e];t(i)})})}},window.Store.init();