window.Store={init(){document.addEventListener("alpine:init",()=>{Alpine.store("global",{user:null,theme:"light",params:{},setUser(u){this.user=u},setTheme(t){this.theme=t}})})},get(k){return Alpine.store("global")[k]},set(k,v){Alpine.store("global")[k]=v},subscribe(k,c){document.addEventListener("alpine:initialized",()=>{Alpine.effect(()=>{const v=Alpine.store("global")[k];c(v)})})}};window.Store.init();
