// Set a value
Store.set('user', { name: 'John', role: 'admin' });

// Get a value
const user = Store.get('user');

// Subscribe to changes
Store.subscribe('user', (newValue) => {
    console.log('User updated:', newValue);
});

// Define a plugin
Framework.plugin('logger', (message) => {
    console.log('[App]', message);
    return message;
});

// Use the plugin
Framework.usePlugin('logger', 'User logged in');

// Check cache first
let users = CoreUI.Cache.get('users_v1');

if (!users) {
    // Fetch from API
    users = await Framework.fetchJSON('data/users.json');

    // Cache for 30 minutes
    CoreUI.Cache.set('users_v1', users, 30 * 60 * 1000);
}

// Clear cache when needed
CorUI.Cache.clear('users_v1');
